import React, { useState } from 'react';

const CadastroClientes = ({ onCadastrarCliente }) => {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [telefone, setTelefone] = useState('');
  const [coordenadaX, setCoordenadaX] = useState(0); // Definindo valor padrão
  const [coordenadaY, setCoordenadaY] = useState(0); // Definindo valor padrão

  const handleCadastrarCliente = async () => {
    const cliente = {
      nome: nome,
      email: email,
      telefone: telefone,
      coordenadaX: coordenadaX, // Adicionando coordenadas ao objeto cliente
      coordenadaY: coordenadaY,
    };

    try {
      await onCadastrarCliente(cliente);
      setNome('');
      setEmail('');
      setTelefone('');
      setCoordenadaX(0); // Resetando as coordenadas após o cadastro
      setCoordenadaY(0);
    } catch (error) {
      console.error('Erro ao cadastrar cliente:', error);
    }
  };

  return (
    <div>
      <h2>Cadastro de Clientes</h2>
      <label>
        Nome:
        <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} />
      </label>
      <label>
        Email:
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <label>
        Telefone:
        <input type="text" value={telefone} onChange={(e) => setTelefone(e.target.value)} />
      </label>
      <label>
        Coordenada X:
        <input type="number" value={coordenadaX} onChange={(e) => setCoordenadaX(e.target.value)} />
      </label>
      <label>
        Coordenada Y:
        <input type="number" value={coordenadaY} onChange={(e) => setCoordenadaY(e.target.value)} />
      </label>
      <button onClick={handleCadastrarCliente}>Cadastrar Cliente</button>
    </div>
  );
};

export default CadastroClientes;
