const CadastroClientes = ({ onCadastrarCliente }) => {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [telefone, setTelefone] = useState('');

  const handleCadastrarCliente = async () => {
    if (!email.includes('@')) {
      alert('Por favor, insira um e-mail válido.');
      return;
    }
  
    const cliente = {
      nome: nome,
      email: email,
      telefone: telefone
    };
  
    try {
      const response = await fetch('http://localhost:3000/clientes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(cliente)
      });
  
      if (response.ok) {
        console.log('Cliente cadastrado com sucesso!');
        onCadastrarCliente(cliente);
      } else {
        // Adicione um tratamento adequado para o erro
        const errorData = await response.json();
        console.error('Erro ao cadastrar cliente:', errorData);
      }
    } catch (error) {
      // Adicione um tratamento adequado para o erro
      console.error('Erro na requisição:', error);
    }
  };
  return (
    <div>
      <h2>Cadastro de Clientes</h2>
      <form>
        <label>
          Nome:
          <input type="text" value={nome} onChange={e => setNome(e.target.value)} />
        </label>
        <label>
          Email:
          <input type="email" value={email} onChange={e => setEmail(e.target.value)} />
        </label>
        <label>
          Telefone:
          <input type="tel" value={telefone} onChange={e => setTelefone(e.target.value)} />
        </label>
        <button type="button" onClick={handleCadastrarCliente}>
          Cadastrar Cliente
        </button>
      </form>
    </div>
  );
};

export default CadastroClientes;
