import './ListagemClientes.css';
import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Tooltip } from 'react-tooltip';

const ListagemClientes = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [clientes, setClientes] = useState([]);
  const [filtro, setFiltro] = useState('');
  const [clientesFiltrados, setClientesFiltrados] = useState([]);
  const [ordemVisita, setOrdemVisita] = useState([]);

  useEffect(() => {
    fetchClientes();
  }, []);

  const fetchClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      const data = await response.json();
      setClientes(data);
      setClientesFiltrados(data);
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  const handleFiltrar = () => {
    const clientesFiltrados = clientes.filter((cliente) =>
      Object.values(cliente).some((value) =>
        String(value).toLowerCase().includes(filtro.toLowerCase())
      )
    );
    setClientesFiltrados(clientesFiltrados);
  };

  const calcularRotaOtimizada = async () => {
    try {
      const response = await fetch('http://localhost:3000/rota-otimizada');
      const rotaOtimizada = await response.json();
      setOrdemVisita(rotaOtimizada);
      handleOpenModal();
    } catch (error) {
      console.error('Erro ao calcular rota otimizada:', error);
    }
  };

  const handleOpenModal = () => {
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="listagem-clientes-container">
      <h2>Listagem de Clientes</h2>
      <div>
        <label>
          Filtro:
          <input
            type="text"
            value={filtro}
            onChange={(e) => setFiltro(e.target.value)}
          />
        </label>
        <button onClick={handleFiltrar}>Filtrar</button>
        <button onClick={calcularRotaOtimizada}>Calcular Rota Otimizada</button>
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={handleCloseModal}
          contentLabel="Exemplo de Modal"
        >
          <h2>Ordem de Visitação dos Clientes</h2>
          <ul>
            {ordemVisita.map((cliente, index) => (
              <li key={cliente.id}>
                {cliente.nome} - Coordenadas: ({cliente.coordenadax}, {cliente.coordenaday})
              </li>
            ))}
          </ul>
          <button onClick={handleCloseModal}>Fechar Modal</button>
        </Modal>
      </div>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Telefone</th>
            <th>Localização</th>
          </tr>
        </thead>
        <tbody>
          {clientesFiltrados.map((cliente, index) => (
            <tr key={cliente.id}>
              <td>{cliente.nome}</td>
              <td>{cliente.email}</td>
              <td>{cliente.telefone}</td>
              <td>
                {ordemVisita[index] ? (
                  <div>
                    <span
                      data-tip={`Coordenadas: (${cliente.coordenadax}, ${cliente.coordenaday})`}
                      data-for={`tooltip-${cliente.id}`}
                    >
                      <i className="fas fa-map-marker-alt"></i>
                    </span>
                    <Tooltip
                      id={`tooltip-${cliente.id}`}
                      place="right"
                      effect="solid"
                      getContent={() => `Coordenadas: (${cliente.coordenadax}, ${cliente.coordenaday})`}
                    />
                  </div>
                ) : (
                  '-'
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListagemClientes;
