import React, { useState } from 'react';
import './CadastroClientes.css';

const CadastroClientes = ({ onCadastrarCliente }) => {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [telefone, setTelefone] = useState('');
  const [coordenadaX, setCoordenadaX] = useState(0);
  const [coordenadaY, setCoordenadaY] = useState(0);
  const [aviso, setAviso] = useState('');

  const handleCadastrarCliente = async () => {
    // Verificando o formato do e-mail
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setAviso('Formato de e-mail inválido');
      return;
    }

    // Verificando se o telefone contém apenas números
    const telefoneValido = /^[0-9]+$/.test(telefone);
    if (!telefoneValido) {
      setAviso('O telefone deve conter apenas números');
      return;
    }

    // Formatando o telefone
    const telefoneFormatado = formatarTelefone(telefone);

    const cliente = {
      nome: nome,
      email: email,
      telefone: telefoneFormatado,
      coordenadaX: coordenadaX,
      coordenadaY: coordenadaY,
    };

    try {
      await onCadastrarCliente(cliente);
      setNome('');
      setEmail('');
      setTelefone('');
      setCoordenadaX(0);
      setCoordenadaY(0);
      setAviso(''); // Limpar aviso após o cadastro bem-sucedido
    } catch (error) {
      console.error('Erro ao cadastrar cliente:', error);
    }
  };

  // Função para formatar o telefone
  const formatarTelefone = (numero) => {
    // Remover caracteres não numéricos
    const numeros = numero.replace(/\D/g, '');

    // Adicionar parênteses para o DDD e inserir 0 após o quinto dígito
    let telefoneFormatado = '';
    if (numeros.length >= 2) {
      telefoneFormatado += `(${numeros.substring(0, 2)})`;
    }
    if (numeros.length > 2) {
      telefoneFormatado += `${numeros.substring(2, 6)}`;
    }
    if (numeros.length >= 6) {
      telefoneFormatado += `-${numeros.substring(6)}`;
    }

    return telefoneFormatado;
  };

  return (
    <div className="cadastro-clientes-container">
      <h2>Cadastro de Clientes</h2>
      {aviso && <div className="aviso">{aviso}</div>}
      <label>
        Nome:
        <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} />
      </label>
      <label>
        Email:
        <input
          type="text"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          pattern="[^\s@]+@[^\s@]+\.[^\s@]+"
        />
      </label>
      <label>
        Telefone:
        <input
          type="tel"
          value={telefone}
          onChange={(e) => setTelefone(e.target.value)}
          pattern="[0-9]*"
          inputMode="numeric"
        />
      </label>
      <label>
        Coordenada X:
        <input type="number" value={coordenadaX} onChange={(e) => setCoordenadaX(e.target.value)} />
      </label>
      <label>
        Coordenada Y:
        <input type="number" value={coordenadaY} onChange={(e) => setCoordenadaY(e.target.value)} />
      </label>
      <button onClick={handleCadastrarCliente}>Cadastrar Cliente</button>
    </div>
  );
};

export default CadastroClientes;
