import React, { useEffect, useState } from 'react';
import ListagemCategorias from './components/ListagemCategorias'; // Importamos o componente de listagem de categorias
import CadastroCategorias from './components/CadastroCategorias'; // Importamos o componente de cadastro de categorias
import Menu from './components/Menu';
import './App.css';

const App = () => {
  const [categorias, setCategorias] = useState([]); // Alteramos o estado para categorias
  const [mostrarListagem, setMostrarListagem] = useState(true);

  useEffect(() => {
    fetchCategorias(); // Chamamos a função para buscar as categorias ao carregar o componente
  }, []);

  const fetchCategorias = async () => {
    try {
      const response = await fetch('http://localhost:3000/categorias'); // Endpoint para buscar categorias
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  const cadastrarCategoria = async (categoria) => {
    try {
      const response = await fetch('http://localhost:3000/categorias', { // Endpoint para cadastrar categoria
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(categoria)
      });

      if (response.ok) {
        console.log('Categoria cadastrada com sucesso!');
        fetchCategorias(); // Atualiza a lista de categorias após o cadastro
      } else {
        console.error('Erro ao cadastrar categoria');
      }
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  const toggleComponente = (componente) => {
    if (componente === 'listagem') {
      setMostrarListagem(true);
    } else if (componente === 'cadastro') {
      setMostrarListagem(false);
    }
  };

  return (
    <div>
      <Menu onToggle={toggleComponente} />
      <div className="app-container">
        {mostrarListagem ? (
          <ListagemCategorias categorias={categorias} /> // Passamos as categorias para o componente de listagem
        ) : (
          <CadastroCategorias onCadastrarCategoria={cadastrarCategoria} /> // Passamos a função de cadastro para o componente de cadastro
        )}
      </div>
    </div>
  );
};

export default App;
