import React, { useEffect, useState } from 'react';
import './ListagemClientes.css';

const ListagemClientes = () => {
  const [clientes, setClientes] = useState([]);
  const [filtro, setFiltro] = useState('');
  const [clientesFiltrados, setClientesFiltrados] = useState([]);

  const fetchClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      const data = await response.json();
      setClientes(data);
      setClientesFiltrados(data);
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  useEffect(() => {
    fetchClientes();
  }, []);

  const handleFiltrar = () => {
    const clientesFiltrados = clientes.filter((cliente) =>
      Object.values(cliente).some((value) =>
        String(value).toLowerCase().includes(filtro.toLowerCase())
      )
    );
    setClientesFiltrados(clientesFiltrados);
  };

  const calcularRotaOtimizada = async () => {
    try {
      const response = await fetch('http://localhost:3000/rota-otimizada');
      const rotaOtimizada = await response.json();
      setClientesFiltrados(rotaOtimizada);
    } catch (error) {
      console.error('Erro ao calcular rota otimizada:', error);
    }
  };

  return (
    <div className="listagem-clientes-container">
      <h2>Listagem de Clientes</h2>
      <div>
        <label>
          Filtro:
          <input
            type="text"
            value={filtro}
            onChange={(e) => setFiltro(e.target.value)}
          />
        </label>
        <button onClick={handleFiltrar}>Filtrar</button>
        <button onClick={calcularRotaOtimizada}>Calcular Rota Otimizada</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Telefone</th>
          </tr>
        </thead>
        <tbody>
          {clientesFiltrados.map((cliente) => (
            <tr key={cliente.id}>
              <td>{cliente.nome}</td>
              <td>{cliente.email}</td>
              <td>{cliente.telefone}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListagemClientes;
