import React, { useEffect, useState } from 'react';
import ListagemClientes from './components/ListagemClientes';
import CadastroClientes from './components/CadastroClientes';
import Menu from './components/Menu';
import './App.css';

const App = () => {
  const [clientes, setClientes] = useState([]);
  const [mostrarListagem, setMostrarListagem] = useState(true);

  useEffect(() => {
    fetchClientes();
  }, []);

  const fetchClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      const data = await response.json();
      setClientes(data);
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  const cadastrarCliente = async (cliente) => {
    try {
      const response = await fetch('http://localhost:3000/clientes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(cliente)
      });

      if (response.ok) {
        console.log('Cliente cadastrado com sucesso!');
        fetchClientes(); // Atualiza a lista de clientes após o cadastro
      } else {
        console.error('Erro ao cadastrar cliente');
      }
    } catch (error) {
      console.error('Erro na requisição:', error);
    }
  };

  const toggleComponente = (componente) => {
    if (componente === 'listagem') {
      setMostrarListagem(true);
    } else if (componente === 'cadastro') {
      setMostrarListagem(false);
    }
  };

  return (
    <div>
      <Menu onToggle={toggleComponente} />
      <div className="app-container">
        {mostrarListagem ? (
          <ListagemClientes clientes={clientes} />
        ) : (
          <CadastroClientes onCadastrarCliente={cadastrarCliente} />
        )}
      </div>
    </div>
  );
};

export default App;
